mapper.select:
select user_name, username, password, create_time, cellphone, company, job_title, mailbox, company_address, desk_phone, company_province, company_city, company_district from users
user_name = #{realName}
and username = #{userName}
and cellphone = #{cellphone}

mapper.update:

update users
set password=#{password},
update_time=#{updateTime}
where username=#{userName}
